CREATE PROCEDURE [dbo].[sp_ApproveCourse]
    @NotificationId UNIQUEIDENTIFIER,
    @ProcessedBy UNIQUEIDENTIFIER
AS
BEGIN
    SET NOCOUNT ON;
    BEGIN TRANSACTION;
    
    BEGIN TRY
        DECLARE @CourseId UNIQUEIDENTIFIER;
        
        -- Láº¥y CourseId tá»« notification
        SELECT @CourseId = [CourseId]
        FROM [dbo].[CourseNotifications]
        WHERE [Id] = @NotificationId;
        
        IF @CourseId IS NOT NULL
        BEGIN
            -- Cáº­p nháº­t status cá»§a khÃ³a há»c thÃ nh 'Ongoing' (capital O) vÃ  ApprovalStatus = 'approved'
            UPDATE [dbo].[Courses]
            SET [Status] = 'Ongoing',
                [ApprovalStatus] = 'approved',
                [LastModificationTime] = GETDATE(),
                [LastModifierId] = @ProcessedBy
            WHERE [Id] = @CourseId;
            
            -- Cáº­p nháº­t notification status thÃ nh 'approved'
            UPDATE [dbo].[CourseNotifications]
            SET [Status] = 'approved',
                [ProcessedTime] = GETDATE(),
                [ProcessedBy] = @ProcessedBy
            WHERE [Id] = @NotificationId;
            
            COMMIT TRANSACTION;
            SELECT 1 AS Success, 'Course approved successfully' AS Message;
        END
        ELSE
        BEGIN
            ROLLBACK TRANSACTION;
            SELECT 0 AS Success, 'Course not found' AS Message;
        END
    END TRY
    BEGIN CATCH
        ROLLBACK TRANSACTION;
        SELECT 0 AS Success, ERROR_MESSAGE() AS Message;
    END CATCH
END

